settings.custom_css:
.page-brands-page [data-aos^=fade][data-aos^=fade]{
opacity: 1!important;
transform: translateY(0)!important;
}
.page-brands-page h2{
font-weight: 400!important;
font-size: 42px!important;
}
.page-brands-page .choose-brand .sf-custom__grid{
padding: 3%  0!important;
column-gap: 60px;
flex-wrap: nowrap;
justify-content: center;
}
.choose-brand .brand-logo, .choose-brand .sf-custom__block{
padding: 0!important;
max-width: 200px;
}
.page-brands-page .img-text, .banners, .choose-brand{
margin: 7% auto;
}
/*.page-brands-page .img-text{
padding-right: 26%!important;
padding-left: 26%!important;
}*/
.page-brands-page .img-text .sf-custom__grid{
justify-content:center;
}
.page-brands-page .img-text .rte p{
font-weight: 400!important;
font-size: 20px;
line-height: 30px;
}
.page-brands-page .img-text .sf-custom__block:last-child{
max-width:650px!important;
}
@media screen and (max-width:768px){
.page-brands-page .sf-hero__inner{
justify-content: center;
}
.page-brands-page .choose-brand .sf-custom__grid{
padding: 3% 0!important;
column-gap: 15px;
}
.page-brands-page .section__heading, .page-brands-page .sf-hero__title{
font-size: 35px!important;
}
.page-brands-page .img-text .rte p{
font-size: 16px;}
}
